    public static BufferedImage resizeImage(final Image image, int width, int height) {
        final BufferedImage bufferedImage = new BufferedImage(width, height, BufferedImage.TYPE_INT_RGB);
        final Graphics2D graphics2D = bufferedImage.createGraphics();
        graphics2D.setComposite(AlphaComposite.Src);
        //below three lines are for RenderingHints for better image quality at cost of higher processing time
        graphics2D.setRenderingHint(RenderingHints.KEY_INTERPOLATION,RenderingHints.VALUE_INTERPOLATION_BILINEAR);
        graphics2D.setRenderingHint(RenderingHints.KEY_RENDERING,RenderingHints.VALUE_RENDER_QUALITY);
        graphics2D.setRenderingHint(RenderingHints.KEY_ANTIALIASING,RenderingHints.VALUE_ANTIALIAS_ON);
        graphics2D.drawImage(image, 0, 0, width, height, null);
        graphics2D.dispose();
        return bufferedImage;
    }

    private static void createPdfFromImage(BufferedImage image) throws IOException, DocumentException {
//        String dest = "/Users/syusuf/Desktop/idcard.pdf";
//        String dest = "/Users/syusuf/Desktop/idcard.pdf";
        String dest = "/Users/syusuf/Desktop/tag.pdf";
        Document document = new Document();
        PdfWriter.getInstance(document, new FileOutputStream(new File(dest)));
        document.open();
//        image.getWidth()
        ByteArrayOutputStream outputStream = new ByteArrayOutputStream();
        ImageIO.write(image, "png", outputStream);

        com.itextpdf.text.Image image1 = com.itextpdf.text.Image.getInstance(outputStream.toByteArray());
//        image1.setWidthPercentage(35);
//        image1.setXYRatio(0.35f);
//        image1.setXYRatio(0.35f);
        image1.setXYRatio(0.45f);

//        image1.scaleToFit(PageSize.A4.getWidth()*0.35f, PageSize.A4.getHeight()*0.35f);
//        image1.scaleToFit(PageSize.A4.getWidth()*0.35f, PageSize.A4.getHeight()*0.35f);
        image1.scaleToFit(PageSize.A4.getWidth()*0.45f, PageSize.A4.getHeight()*0.45f);
        float x = (PageSize.A4.getWidth() - image1.getScaledWidth()) / 2;
        float y = (PageSize.A4.getHeight() - image1.getScaledHeight()) / 2;
        image1.setAbsolutePosition(x, y);
        document.add(image1);
        document.close();
    }

